sys_script_include.script:
var MetricLoader = Class.create();
MetricLoader.prototype = {
    initialize: function() {
    },

    loadMetricsFromJSON: function(metricJSON) {
        gs.info("Beginning loading metrics from JSON");
        this.loadMetricsFromObject(JSON.parse(metricJSON));
        gs.info("Finishing loading metrics from JSON");
    },

    loadMetricsFromObject: function(metricsArr) {
        gs.info("Beginning loading metrics from object");
        metricsArr = metricsArr["result"];
        for(var i = 0; i < metricsArr.length; i++) {			
            var metric = metricsArr[i];
			if(metric["seriesRef"] == undefined || metric["seriesRef"] == null) continue;

            var sys_id = metric["seriesRef"]["subject"];
            var table = metric["seriesRef"]["table"];
            var metricName = metric["seriesRef"]["metric"];
            var time = new GlideDateTime(metric["values"]["timestamp"]);
            var value = metric["values"]["value"];

            var tableRecord = new GlideRecord(table);
            tableRecord.initialize();
            tableRecord.get(sys_id);

            var dataBuilder = new sn_clotho.DataBuilder(tableRecord, metricName.toString());
            dataBuilder.add(time, parseFloat(value));
			new sn_clotho.Client().put(dataBuilder);
        }
        gs.info("Finishing loading metrics from object");
    },

    type: 'MetricLoader'
};
